namespace B2W\SkyHub\Contract\Entity\Order;

/**
 * Interface PaymentEntityInterface
 * @package B2W\SkyHub\Contract\Entity\Order
 */
interface PaymentEntityInterface
{
    /**
     * @return float
     */
    public function getValue();

    /**
     * @param $value
     * @return float
     */
    public function setValue($value);

    /**
     * @return \DateTime
     */
    public function getTransactionDate();

    /**
     * @param $transactionDate
     * @return \DateTime
     */
    public function setTransactionDate($transactionDate);

    /**
     * @return string
     */
    public function getStatus();

    /**
     * @param $status
     * @return string
     */
    public function setStatus($status);

    /**
     * @return SefazEntity
     */
    public function getSefaz();

    /**
     * @param $sefaz
     * @return SefazEntity
     */
    public function setSefaz(SefazEntity $sefaz);

    /**
     * @return string
     */
    public function getParcels();

    /**
     * @param $parcels
     * @return string
     */
    public function setParcels($parcels);

    /**
     * @return string
     */
    public function getMethod();

    /**
     * @param $method
     * @return string
     */
    public function setMethod($method);

    /**
     * @return string
     */
    public function getDescription();

    /**
     * @param $description
     * @return string
     */
    public function setDescription($description);

    /**
     * @return string
     */
    public function getCardIssuer();

    /**
     * @param $cardIssuer
     * @return string
     */
    public function setCardIssuer($cardIssuer);

    /**
     * @return string
     */
    public function getAutorizationId();

    /**
     * @param $autorizationId
     * @return string
     */
    public function setAutorizationId($autorizationId);
}
