namespace B2W\SkyHub\Contracts\Sales\Order\Status;

/**
 * Interface Entity
 * @package B2W\SkyHub\Contracts\Sales\Order\Status
 */
interface Entity
{
    /**
     * @return string
     */
    public function getCode();

    /**
     * @param $code
     * @return string
     */
    public function setCode($code);

    /**
     * @return string
     */
    public function getLabel();

    /**
     * @param $label
     * @return string
     */
    public function setLabel($label);

    /**
     * @return string
     */
    public function getType();

    /**
     * @param $type
     * @return string
     */
    public function setType($type);
}
