namespace B2W\SkyHub\Helper;

/**
 * Class App
 * @package B2W\SkyHub\Helper
 */
class App
{
    /**
     * @param $class
     * @param $attr
     * @return mixed
     */
    public function getSetterMethodName($class, $attr)
    {
        $name = 'set' . implode('', array_map(function($n) {
                return ucfirst($n);
            }, explode('_', $attr)));

        if (method_exists($class, $name)) {
            return $name;
        }

        return false;
    }
}
