namespace B2W\SkyHub\Model\Transformer\Catalog\Product\Price;

use B2W\SkyHub\Model\Transformer\PostToEntityAbstract;

/**
 * Class PostToEntity
 * @package B2W\SkyHub\Model\Transformer\Catalog\Product\Price
 */
class PostToEntity extends PostToEntityAbstract
{
    /**
     * @return array|mixed
     */
    protected function _getAttributeMap()
    {
        return array();
    }

    /**
     * @return mixed|null
     */
    public function convert()
    {
        if (!isset($this->_meta['_price'])) {
            return null;
        }

        return count($this->_meta['_price']) > 1
            ? null
            : current($this->_meta['_price']);
    }
}
