namespace B2W\SkyHub\Model\Transformer\Catalog\Product\Type;

use B2W\SkyHub\Model\Catalog\Product\Entity;

/**
 * Class Api
 * @package B2W\SkyHub\Model\Transformer\Catalog\Product\Type\Standard
 */
class Standard
{
    /**
     * @param Entity $product
     * @param \SkyHub\Api\EntityInterface\Catalog\Product $interface
     * @throws \Exception
     */
    public static function create(Entity $product, \SkyHub\Api\EntityInterface\Catalog\Product $interface)
    {
        static $instance = false;
        if ($instance === false) {
            $instance = new static();
        }

        $instance->_prepareVariationAttributes($product, $interface);

        foreach ($product->getVariations() as $variation) {

            $variationInterface = $interface->addVariation(
                $variation->getSku(),
                $variation->getQty(),
                $variation->getEan()
            );

            $instance->_prepareImages($variation, $variationInterface);

            $instance->_prepareSpecifications($variation, $variationInterface);

            \App::dispatchEvent(
                'skyhub_product_variation_create_after',
                array(
                    'product'   => $product,
                    'variation' => $variation,
                )
            );
        }
    }

    /**
     * @param \B2W\SkyHub\Model\Catalog\Product\Variation\Entity $variation
     * @param \SkyHub\Api\EntityInterface\Catalog\Product\Variation $interface
     * @return $this
     */
    protected function _prepareImages(
        \B2W\SkyHub\Model\Catalog\Product\Variation\Entity $variation,
        \SkyHub\Api\EntityInterface\Catalog\Product\Variation $interface
    )
    {
        foreach ($variation->getImages() as $image) {
            $interface->addImage($image);
        }

        return $this;
    }

    /**
     * @param \B2W\SkyHub\Model\Catalog\Product\Variation\Entity $variation
     * @param \SkyHub\Api\EntityInterface\Catalog\Product\Variation $interface
     * @return $this
     * @throws \Exception
     */
    protected function _prepareSpecifications(
        \B2W\SkyHub\Model\Catalog\Product\Variation\Entity $variation,
        \SkyHub\Api\EntityInterface\Catalog\Product\Variation $interface
    )
    {
        /** @var \B2W\SkyHub\Contracts\Catalog\Product\Specification\Entity $specification */
        foreach ($variation->getSpecifications() as $specification) {
            $interface->addSpecification(
                $specification->getAttribute()->getCode(),
                $specification->getOption()->getLabel()
            );
        }

        return $this;
    }

    /**
     * @param Entity $product
     * @param \SkyHub\Api\EntityInterface\Catalog\Product $interface
     * @return $this
     * @throws \Exception
     */
    protected function _prepareVariationAttributes(
        Entity $product,
        \SkyHub\Api\EntityInterface\Catalog\Product $interface
    )
    {
        /** @var \B2W\SkyHub\Model\Catalog\Product\Attribute\Entity $attribute */
        foreach ($product->getVariationAttributes() as $attribute) {
            $interface->addVariationAttribute($attribute->getCode());
        }

        return $this;
    }
}