namespace B2W\SkyHub\Model\Transformer\Catalog\Product\VariationAttributes;

use B2W\SkyHub\Contracts\Catalog\Product\Specification\Entity;
use B2W\SkyHub\Model\Catalog\Product\Attribute\Collection;
use B2W\SkyHub\Model\Transformer\PostToEntityAbstract;

/**
 * Class PostToEntity
 * @package B2W\SkyHub\Model\Transformer\Catalog\Product\VariationAttributes
 */
class PostToEntity extends PostToEntityAbstract
{
    /**
     * @return array|mixed
     */
    protected function _getAttributeMap()
    {
        return array();
    }

    /**
     * @return Collection|null
     */
    public function convert()
    {
        $collection = new Collection();
        $attrs      = array();

        foreach ($this->_parentEntity->getVariations() as $variation) {
            /** @var Entity $specification */
            foreach ($variation->getSpecifications() as $specification) {
                /** @var \B2W\Skyhub\Contracts\Catalog\Product\Attribute\Entity $attr */
                $attr = $specification->getAttribute();
                if (!isset($attrs[$attr->getCode()])) {
                    $attrs[$attr->getCode()] = $attr;
                }
            }
        }

        foreach ($attrs as $attr) {
            $collection->addItem($attr);
        }

        return $collection;
    }
}
