namespace B2W\SkyHub\Model\Transformer\Sales\Order;


use B2W\SkyHub\Model\Sales\Order\Status\Entity;

class Status
{
    /** @var Entity */
    protected $_entity      = null;
    protected $_wcStatus    = null;
    final public static function convert(Entity $status, $wcStatus)
    {
        static $instance = false;
        if ($instance === false) {
            $instance = new static();
        }

        $instance->_entity      = $status;
        $instance->_wcStatus    = $wcStatus;

        $instance->_convert();
        $instance->_reset();

        return $instance;
    }

    protected function _reset()
    {
        $this->_entity      = null;
        $this->_wcStatus    = null;

        return $this;
    }

    protected function _convert()
    {
        $this->_entity->setType('teste');
        $this->_entity->setLabel('Teste');
        $this->_entity->setCode($this->_wcStatus);

        return $this;
    }
}
