namespace B2W\SkyHub\Model\Transformer\Sales\Order\Status;

use B2W\SkyHub\Model\Sales\Order\Status\Entity;
use B2W\SkyHub\Model\Transformer\ApiToEntityAbstract;

/**
 * Class ApiToEntity
 * @package B2W\SkyHub\Model\Transformer\Sales\Order\Status
 */
class ApiToEntity extends ApiToEntityAbstract
{
    /**
     * @return mixed|null
     */
    protected function _getEntityInstance()
    {
        return null;
    }

    /**
     * @return mixed|null
     */
    protected function _getMapInstance()
    {
        return null;
    }

    /**
     * @return Entity|null
     */
    public function convert()
    {
        $data = $this->_prepareData();
        $status = new Entity();
        $status->setType($data['type']);
        $status->setCode($data['code']);
        $status->setLabel($data['label']);

        return $status;
    }
}
