namespace B2W\SkyHub\Model;

/**
 * Class TransformerAbstract
 * @package B2W\SkyHub\Model
 */
abstract class TransformerAbstract
{
    /**
     * @return TransformerAbstract|bool
     */
    static protected function _instantiate()
    {
        static $instance = false;
        if ($instance === false) {
            $instance = new static();
        }

        return $instance;
    }
}
